<script>


    let products = document.getElementsByClassName(""catalog"")[0];
    console.log(products);
    products.addEventListener(""click"", function (e) {
        e.stopPropagation();
        let target = e.target;


        let targetId = target.classList.contains(""productItem"") ? target.value : null;
        console.log(targetId);

        if (targetId != null) {
            fetch(""/Procurement/AddProduct?productId="" + targetId, {
                method: ""post"",

            })


        }


    })


    $('.productItem').click(function () {
        setTimeout(function () {

            $(""#msg"").show().fadeOut(3000);
        }, 400)
    })






    $(function () {
        $(""#addProductModal"").modal();
    });

    $(""#uploadImage"").change(function (e) {
        e.preventDefault();
        var element = this;
        var formInfo = new FormData();
        var totalFiles = element.files.length;

        for (i = 0; i < totalFiles; i++");
            WriteLiteral(@") {
            var file = element.files[i];
            formInfo.append(""ImageUrl"", file);
        }

        $.ajax({
            type: ""POST"",
            url: ""/Product/UploadProduct"",
            data: formInfo,
            dataType: ""json"",
            contentType: false,
            processData: false,
            success: function (response) {

                console.log(response);
                if (response) {

                    alert(""Success"");


                }
            },
            error: function () {
                console.log(""Error"");
            }
        });
    });

    $('#upload').submit(function (e) {
        e.preventDefault();

        $.ajax({
            url: this.action,
            type: this.method,
            data: new FormData(this),
            cache: false,
            contentType: false,
            processData: false,
            success: function (data) {
                console.log(data.UploadedFileCount + ' file(s) upl");
            WriteLiteral(@"oaded successfully');
                $(""#ImageUrl"").show();

            },
            error: function (xhr, error, status) {
                console.log(error, status);
            }
        });
    });


    $(document).ready(function () {
        $(""#searchProduct"").keyup(function () {

            var value = $(this).val().toLowerCase();

            console.log(value);
            $(""#productsTable .card"").filter(function () {
                $(this).toggle($(this).text().toLowerCase().indexOf(value) > -1)
            });
        });
    });


</script>");
